import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../redux/store';
import { addPost } from '../redux/postsSlice';
import './FeedPage.css'; // Add appropriate CSS styles here
impo

const FeedPage: React.FC = () => {
  const [newPost, setNewPost] = useState('');
  const posts = useSelector((state: RootState) => state.posts.posts);
  const dispatch = useDispatch();

  const handleCreatePost = () => {
    if (newPost.trim()) {
      dispatch(addPost({ content: newPost, image: '', tags: '' }));
      setNewPost('');
    }
  };

  return (
    <div className="feed-layout">
      {/* Left Sidebar */}
      <aside className="left-sidebar">
        <div className="user-card">
          <img src="{}" alt="Profile" />
          <h3>Pradeep Yadav</h3>
          <p>Frontend Developer @ Dotvik Solutions</p>
          <p>Bachelor of Technology in Computer Science</p>
          <div className="profile-analytics">
            <p>Profile viewers: 37</p>
            <p>View all analytics</p>
          </div>
          <a href="#">Saved items</a>
          <div className="recent-groups">
            <h4>Recent</h4>
            <ul>
              <li>Premium Career Group</li>
              <li>React Js & React Native Devs</li>
            </ul>
          </div>
        </div>
      </aside>

      {/* Main Feed Section */}
      <main className="main-feed">
        <div className="post-create-box">
          <textarea
            placeholder="Start a post"
            value={newPost}
            onChange={(e) => setNewPost(e.target.value)}
          />
          <button onClick={handleCreatePost}>Post</button>
        </div>
        <ul className="feed-posts">
          {posts.map((post) => (
            <li key={post.id} className="post-item">
              <div className="post-header">
                <img src="path_to_user_profile" alt="User" />
                <div className="post-user-info">
                  <h4>{post.userName}</h4>
                  <p>{post.userTitle}</p>
                </div>
              </div>
              {post.image && <img src={post.image} alt="Post visual" />}
              <p>{post.content}</p>
              <div className="post-actions">
                <button>Like ({post.likes})</button>
                <button>Comment</button>
                <button>Share</button>
              </div>
            </li>
          ))}
        </ul>
      </main>

      {/* Right Sidebar */}
      <aside className="right-sidebar">
        <div className="news-section">
          <h4>LinkedIn News</h4>
          <ul>
            <li>Black Box to hire 400 people</li>
            <li>LIC and Infosys join hands</li>
            <li>Where Indians are moving for work</li>
          </ul>
        </div>
        <div className="widgets-section">
          <h4>Today's games</h4>
          <ul>
            <li>Queens (6 connections played)</li>
            <li>Pinpoint (3 connections played)</li>
          </ul>
        </div>
      </aside>
    </div>
  );
};

export default FeedPage;
