import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface UserProfile {
  name: string;
  bio: string;
  profilePicture: string;
  title: string;
  location: string;
  phoneNumber: string;  // Include phone number if required
}

interface UserState {
  profile: UserProfile | null;
}

const initialState: UserState = {
  profile: {
    name: 'John Doe',
    bio: 'Software Engineer at LinkedIn Clone',
    profilePicture: 'https://example.com/profile-pic.jpg',
    title: 'Frontend Developer',
    location: 'San Francisco, CA',
    phoneNumber: '1234567890',
  },
};

const userSlice = createSlice({
  name: 'user',
  initialState,
  reducers: {
    setUserProfile: (state, action: PayloadAction<UserProfile>) => {
      state.profile = action.payload;
    },
    login: (state, action: PayloadAction<{ phoneNumber: string }>) => {
      // Mock login logic: Update the profile based on phone number
      state.profile = {
        name: 'John Doe',
        bio: 'Software Engineer at LinkedIn Clone',
        profilePicture: 'https://example.com/profile-pic.jpg',
        title: 'Frontend Developer',
        location: 'San Francisco, CA',
        phoneNumber: action.payload.phoneNumber,
      };
    },
  },
});

export const { setUserProfile, login } = userSlice.actions;
export default userSlice.reducer;
