// src/pages/FeedPage.tsx
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../redux/store'; // Adjust the import based on your project structure
import { addPost } from '../redux/postsSlice';
import './FeedPage.css';

const FeedPage: React.FC = () => {
  const [newPostContent, setNewPostContent] = useState('');
  const [newPostImage, setNewPostImage] = useState('');
  const [newPostTags, setNewPostTags] = useState('');
  const dispatch = useDispatch();
  const posts = useSelector((state: RootState) => state.posts.posts);

  const handleCreatePost = () => {
    if (newPostContent.trim()) {
      dispatch(addPost({ content: newPostContent, image: newPostImage, tags: newPostTags }));
      setNewPostContent('');
      setNewPostImage('');
      setNewPostTags('');
    }
  };

  return (
    <div className="feed-page">
      <div className="create-post-section">
        <textarea
          className="create-post-textarea"
          placeholder="What's on your mind?"
          value={newPostContent}
          onChange={(e) => setNewPostContent(e.target.value)}
        />
        <input
          type="text"
          className="create-post-input"
          placeholder="Image URL"
          value={newPostImage}
          onChange={(e) => setNewPostImage(e.target.value)}
        />
        <input
          type="text"
          className="create-post-input"
          placeholder="Tags"
          value={newPostTags}
          onChange={(e) => setNewPostTags(e.target.value)}
        />
        <button onClick={handleCreatePost} className="create-post-button">Post</button>
      </div>
      <div className="posts-list">
        {posts.map((post) => (
          <div key={post.id} className="post">
            <p>{post.content}</p>
            {post.image && <img src={post.image} alt="Post image" />}
            <p>{post.tags}</p>
            <p>Likes: {post.likes}</p>
            <div>
              {post.comments.map((comment, index) => (
                <p key={index}>{comment}</p>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FeedPage;
