// src/pages/LoginPage.tsx
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { login } from '../redux/userSlice'; // Import the login action
import './LoginPage.css';

const LoginPage: React.FC = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [otp, setOtp] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleOtpSubmit = () => {
    if (otp === '1234') {
      dispatch(
        login({
          name: 'Pradeep Yadav',
          title: 'Software Engineer',
          location: 'Saket, India',
          profilePicture: '/assets/pp.jpg',
          bio: 'Passionate software engineer with 2.6 years of experience.',
          phoneNumber, // Include phone number in the profile
        })
      );
      navigate('/feed');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Phone Number"
        value={phoneNumber}
        onChange={(e) => setPhoneNumber(e.target.value)}
      />
      <input
        type="text"
        placeholder="OTP"
        value={otp}
        onChange={(e) => setOtp(e.target.value)}
      />
      <button onClick={handleOtpSubmit}>Submit OTP</button>
    </div>
  );
};

export default LoginPage;
