// src/pages/LoginPage.tsx
import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { login } from '../redux/userSlice'; // Import the login action
import './LoginPage.css';

const LoginPage: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleLogin = () => {
    // Simulate login by dispatching the login action with user data
    const dummyProfile = {
      name: 'Pradeep Yadav',
      title: 'Software Engineer',
      location: 'Saket, India',
      profilePicture: '/assets/pp.jpg',
      bio: 'Passionate software engineer with 2.6 years of experience.',
    };
    dispatch(login(dummyProfile));
    navigate('/feed'); // Navigate to feed after login
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default LoginPage;
