import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../redux/store'; // Adjust the import as per your setup
import { likePost, addComment } from '../redux/postsSlice';
import './FeedPage.css'; // Assuming there's a CSS file for styling the feed page

const FeedPage: React.FC = () => {
  const posts = useSelector((state: RootState) => state.posts.posts); // Selecting posts from state
  const dispatch = useDispatch();

  const handleLike = (postId: number) => {
    dispatch(likePost(postId));
  };

  return (
    <div className="feed-page">
      <ul className="feed-posts">
        {posts.map((post) => (
          <li key={post.id} className="post-item">
            <div className="post-header">
              <img src="path_to_user_profile" alt="User" className="user-profile-pic" />
              <div className="post-user-info">
                <h4>{post.userName}</h4>
                <p>{post.userTitle}</p>
              </div>
            </div>
            {post.image && <img src={post.image} alt="Post visual" className="post-image" />}
            <p>{post.content}</p>
            <div className="post-tags">{post.tags}</div>
            <div className="post-actions">
              <button onClick={() => handleLike(post.id)}>Like ({post.likes})</button>
              <button>Comment</button>
              <button>Share</button>
            </div>
            <ul className="post-comments">
              {post.comments.map((comment, index) => (
                <li key={index}>{comment}</li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FeedPage;
