import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../redux/store';
import { addPost, likePost, addComment } from '../redux/postsSlice';
import './FeedPage.css'; // Import the CSS styles

const FeedPage: React.FC = () => {
  const posts = useSelector((state: RootState) => state.posts.posts);
  const dispatch = useDispatch();
  const [newPost, setNewPost] = useState('');
  const [image, setImage] = useState('');
  const [tags, setTags] = useState('');

  const handleCreatePost = () => {
    if (newPost.trim()) {
      dispatch(addPost({ content: newPost, image, tags }));
      setNewPost('');
      setImage('');
      setTags('');
    }
  };

  const handleLike = (id: number) => {
    dispatch(likePost(id));
  };

  const handleComment = (id: number, comment: string) => {
    dispatch(addComment({ postId: id, comment }));
  };

  return (
    <div className="feed-container">
      <div className="create-post">
        <textarea
          className="create-post-textarea"
          placeholder="What's on your mind?"
          value={newPost}
          onChange={(e) => setNewPost(e.target.value)}
        />
        <input
          type="text"
          placeholder="Image URL (optional)"
          value={image}
          onChange={(e) => setImage(e.target.value)}
        />
        <input
          type="text"
          placeholder="Tags (optional, comma separated)"
          value={tags}
          onChange={(e) => setTags(e.target.value)}
        />
        <button className="create-post-button" onClick={handleCreatePost}>Post</button>
      </div>

      <div className="posts">
        {posts.map((post) => (
          <div key={post.id} className="post">
            {post.image && <img src={post.image} alt="Post visual" className="post-image" />}
            <div className="post-content">
              <p>{post.content}</p>
              <button className="like-button" onClick={() => handleLike(post.id)}>
                Like ({post.likes})
              </button>
            </div>
            <div className="comments-section">
              <input
                className="comment-input"
                type="text"
                placeholder="Write a comment..."
                onKeyDown={(e) => {
                  if (e.key === 'Enter') {
                    handleComment(post.id, (e.target as HTMLInputElement).value);
                    (e.target as HTMLInputElement).value = '';
                  }
                }}
              />
              <ul className="comments-list">
                {post.comments.map((comment, idx) => (
                  <li key={idx} className="comment">{comment}</li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FeedPage;
