// src/pages/FeedPage.tsx
import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { RootState } from '../redux/store';
import { addPost, likePost, addComment, sharePost, editPost } from '../redux/postsSlice';
import './FeedPage.css';

const FeedPage: React.FC = () => {
  const [newPostContent, setNewPostContent] = useState('');
  const [newPostImage, setNewPostImage] = useState('');
  const [editingPostId, setEditingPostId] = useState<number | null>(null);
  const [editContent, setEditContent] = useState('');
  const [comment, setComment] = useState('');

  const posts = useSelector((state: RootState) => state.posts.posts);
  const dispatch = useDispatch();

  const handleCreatePost = () => {
    if (newPostContent.trim()) {
      dispatch(addPost({ content: newPostContent, image: newPostImage, tags: '' }));
      setNewPostContent('');
      setNewPostImage('');
    }
  };

  const handleEditPost = (postId: number) => {
    if (editContent.trim()) {
      dispatch(editPost({ postId, content: editContent }));
      setEditingPostId(null);
    }
  };

  const handleAddComment = (postId: number) => {
    if (comment.trim()) {
      dispatch(addComment({ postId, comment }));
      setComment('');
    }
  };

  return (
    <div className="feed-container">
      <div className="feed-create-post">
        <h2 className="feed-heading">What’s on your mind?</h2>
        <textarea
          value={newPostContent}
          onChange={(e) => setNewPostContent(e.target.value)}
          placeholder="Write a post..."
        />
        <input
          type="text"
          value={newPostImage}
          onChange={(e) => setNewPostImage(e.target.value)}
          placeholder="Add an image URL..."
        />
        <button onClick={handleCreatePost}>Post</button>
      </div>
      <section className="feed-posts">
        <h2 className="feed-heading">Posts</h2>
        <ul className="posts-list">
          {posts.length === 0 ? (
            <li className="no-posts">No posts yet. Be the first to share!</li>
          ) : (
            posts.map((post) => (
              <li key={post.id} className="post-item">
                {editingPostId === post.id ? (
                  <>
                    <textarea
                      value={editContent}
                      onChange={(e) => setEditContent(e.target.value)}
                    />
                    <button onClick={() => handleEditPost(post.id)}>Save</button>
                  </>
                ) : (
                  <>
                    {post.image && <img src={post.image} alt="Post visual" className="post-image" />}
                    <p className="post-content">{post.content}</p>
                    <button onClick={() => dispatch(likePost(post.id))}>
                      Like ({post.likes})
                    </button>
                    <button onClick={() => setEditingPostId(post.id)}>
                      Edit
                    </button>
                    <button onClick={() => dispatch(sharePost(post.id))}>
                      Share ({post.shares})
                    </button>
                    <div className="post-comments">
                      <h4>Comments</h4>
                      <ul>
                        {post.comments.map((comment, index) => (
                          <li key={index}>{comment}</li>
                        ))}
                      </ul>
                      <input
                        type="text"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        placeholder="Add a comment..."
                      />
                      <button onClick={() => handleAddComment(post.id)}>Comment</button>
                    </div>
                  </>
                )}
              </li>
            ))
          )}
        </ul>
      </section>
    </div>
  );
};

export default FeedPage;
