import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface Post {
  id: number;
  content: string;
  likes: number;
  comments: string[];
}

interface PostsState {
  posts: Post[];
}

const initialState: PostsState = {
  posts: [],
};

const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {
    addPost(state: PostsState, action: PayloadAction<string>) {
      state.posts.push({
        id: state.posts.length + 1,
        content: action.payload,
        likes: 0,
        comments: [],
      });
    },
    likePost(state: PostsState, action: PayloadAction<number>) {
      const post = state.posts.find((p) => p.id === action.payload);
      if (post) post.likes++;
    },
    addComment(state: PostsState, action: PayloadAction<{ postId: number; comment: string }>) {
      const post = state.posts.find((p) => p.id === action.payload.postId);
      if (post) post.comments.push(action.payload.comment);
    },
  },
});

export const { addPost, likePost, addComment } = postsSlice.actions;
export default postsSlice.reducer;
