// src/redux/userSlice.ts
import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface UserState {
  profile: {
    name: string;
    title: string;
    location: string;
    profilePicture: string;
    bio?: string;
  } | null;
}

const initialState: UserState = {
  profile: null,
};

const userSlice = createSlice({
  name: 'user',
  initialState,
  reducers: {
    setProfile: (state, action: PayloadAction<UserState['profile']>) => {
      state.profile = action.payload;
    },
    login: (state, action: PayloadAction<UserState['profile']>) => {
      state.profile = action.payload; // Simulate login by setting the profile
    },
    logout: (state) => {
      state.profile = null; // Clear user profile on logout
    },
  },
});

export const { setProfile, login, logout } = userSlice.actions;
export default userSlice.reducer;
