import React from 'react';
import { useSelector } from 'react-redux';
import { RootState } from '../redux/store';
import './ProfilePage.css';

const ProfilePage: React.FC = () => {
  const profile = useSelector((state: RootState) => state.user.profile);
  const posts = useSelector((state: RootState) => state.posts.posts);

  console.log('Profile:', profile); // Debugging line to check profile data

  if (!profile) {
    return <div>Loading...</div>;
  }

  return (
    <div className="profile-container">
      <header className="profile-header">
        <div className="profile-cover">
          <img
            src="/path/to/cover-image.jpg"
            alt="Cover"
            className="cover-image"
          />
        </div>
        <div className="profile-info">
          <div className="profile-image">
            <img
              src={profile.profilePicture || '/default-profile.jpg'}
              alt={profile.name}
              className="profile-picture"
            />
          </div>
          <div className="profile-details">
            <h1>{profile.name}</h1>
            <p className="profile-title">{profile.title}</p>
            <p className="profile-location">{profile.location}</p>
          </div>
        </div>
      </header>
      <section className="profile-bio">
        <h2>About</h2>
        <p>{profile.bio || 'No bio available'}</p>
      </section>
      <section className="profile-posts">
        <h2>Your Posts</h2>
        <ul className="posts-list">
          {posts.length === 0 ? (
            <li className="no-posts">No posts available.</li>
          ) : (
            posts.map((post) => (
              <li key={post.id} className="post-item">
                {post.image && <img src={post.image} alt="Post visual" className="post-image" />}
                <p>{post.content}</p>
              </li>
            ))
          )}
        </ul>
      </section>
    </div>
  );
};

export default ProfilePage;
