// src/redux/postsSlice.ts
import { createSlice, PayloadAction } from '@reduxjs/toolkit';

interface Post {
  id: number;
  content: string;
  image: string;
  tags: string;
  likes: number;
  comments: string[];
}

interface PostState {
  posts: Post[];
}

const initialState: PostState = {
  posts: [
    {
      id: 1,
      content: 'This is my first post!',
      image:{},
      tags: '#first #post',
      likes: 10,
      comments: ['Great post!', 'Congrats on your first post!'],
    },
  ],
};

const postsSlice = createSlice({
  name: 'posts',
  initialState,
  reducers: {
    addPost: (state, action: PayloadAction<{ content: string; image: string; tags: string }>) => {
      const newPost: Post = {
        id: state.posts.length + 1,
        content: action.payload.content,
        image: action.payload.image,
        tags: action.payload.tags,
        likes: 0,
        comments: [],
      };
      state.posts.push(newPost);
    },
    likePost: (state, action: PayloadAction<number>) => {
      const post = state.posts.find((p) => p.id === action.payload);
      if (post) {
        post.likes += 1;
      }
    },
    addComment: (state, action: PayloadAction<{ postId: number; comment: string }>) => {
      const post = state.posts.find((p) => p.id === action.payload.postId);
      if (post) {
        post.comments.push(action.payload.comment);
      }
    },
  },
});

export const { addPost, likePost, addComment } = postsSlice.actions;
export default postsSlice.reducer;
