import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../redux/store';
import { setProfile } from '../redux/userSlice'; // Import action to set profile
import './ProfilePage.css';

// Define Profile type
interface Profile {
  name: string;
  title: string;
  location: string;
  profilePicture: string;
  bio?: string; // Make bio optional
}

// Dummy function to simulate fetching profile data
const fetchProfileData = (): Promise<Profile> => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        name: 'Pradeep Yadav ',
        title: 'Software Engineer',
        location: 'San Francisco, CA',
        profilePicture: '/assets/pp.jpg',
        bio: 'Passionate software engineer with 10 years of experience.',
      });
    }, 1000); // Simulate a 1-second fetch time
  });
};

const ProfilePage: React.FC = () => {
  const profile = useSelector((state: RootState) => state.user.profile);
  const loading = useSelector((state: RootState) => state.user.loading);
  const dispatch = useDispatch();

  useEffect(() => {
    // Simulate fetching profile data when the component mounts
    const loadProfile = async () => {
      dispatch(setProfile({ name: '', title: '', location: '', profilePicture: '' })); // Set initial state
      const profileData = await fetchProfileData();
      dispatch(setProfile(profileData)); // Update profile data
    };

    loadProfile();
  }, [dispatch]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!profile) {
    return <div>No profile available.</div>;
  }

  return (
    <div className="profile-container">
      <header className="profile-header">
        <div className="profile-cover">
          <img src="/assets/pp.jpg" alt="Cover" className="cover-image" />
        </div>
        <div className="profile-info">
          <div className="profile-image">
            <img src={profile.profilePicture} alt={profile.name} className="profile-picture" />
          </div>
          <div className="profile-details">
            <h1>{profile.name}</h1>
            <p className="profile-title">{profile.title}</p>
            <p className="profile-location">{profile.location}</p>
          </div>
        </div>
      </header>
      <section className="profile-bio">
        <h2>About</h2>
        <p>{profile.bio ?? 'No bio available'}</p>
      </section>
      <section className="profile-posts">
        <h2>Your Posts</h2>
        <ul className="posts-list">
          {/* Display user posts */}
        </ul>
      </section>
    </div>
  );
};

export default ProfilePage;
